import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  MapPin, 
  Calendar, 
  DollarSign, 
  Users, 
  Wand2, 
  Plus, 
  ArrowLeft,
  Loader2,
  Heart,
  Camera,
  Utensils,
  Mountain,
  Building,
  Compass
} from 'lucide-react';
import { Button } from '../components/ui/Button.jsx';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/Card.jsx';
import { Input } from '../components/ui/Input.jsx';
import { Badge } from '../components/ui/Badge.jsx';
import { useAuth } from '../contexts/AuthContext.jsx';
import api from '../lib/api.js';

const NewPlanPage = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [selectedActivities, setSelectedActivities] = useState([]);
  
  const [formData, setFormData] = useState({
    nameoftheplace: '',
    fromdate: '',
    todate: '',
    budget: '',
    abouttheplace: '',
    companion: '',
    activityPreferences: []
  });

  const activities = [
    { id: 'sightseeing', label: 'Sightseeing', icon: Camera },
    { id: 'adventure', label: 'Adventure', icon: Mountain },
    { id: 'food', label: 'Food & Dining', icon: Utensils },
    { id: 'culture', label: 'Culture & History', icon: Building },
    { id: 'nature', label: 'Nature & Wildlife', icon: Compass },
    { id: 'relaxation', label: 'Relaxation', icon: Heart }
  ];

  const companions = [
    { id: 'solo', label: 'Solo Travel' },
    { id: 'couple', label: 'Couple' },
    { id: 'family', label: 'Family' },
    { id: 'friends', label: 'Friends' },
    { id: 'business', label: 'Business' }
  ];

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const toggleActivity = (activityId) => {
    setSelectedActivities(prev => {
      const updated = prev.includes(activityId) 
        ? prev.filter(id => id !== activityId)
        : [...prev, activityId];
      
      setFormData(prevForm => ({
        ...prevForm,
        activityPreferences: updated
      }));
      
      return updated;
    });
  };

  const validateForm = () => {
    if (!formData.nameoftheplace.trim()) {
      alert('Please enter a destination');
      return false;
    }
    if (!formData.fromdate || !formData.todate) {
      alert('Please select travel dates');
      return false;
    }
    if (new Date(formData.fromdate) >= new Date(formData.todate)) {
      alert('End date must be after start date');
      return false;
    }
    return true;
  };

  const handleCreatePlan = async (withAI = false) => {
    if (!validateForm()) return;

    setLoading(true);
    try {
      const planData = {
        ...formData,
        user_id: user.id,
        budget: formData.budget ? parseFloat(formData.budget) : null,
        activityPreferences: selectedActivities,
        useAI: withAI
      };

      const response = await api.post('/api/plans', planData);
      
      if (response.data.success) {
        navigate(`/plans/${response.data.plan.id}/plan`);
      } else {
        alert('Failed to create plan. Please try again.');
      }
    } catch (error) {
      console.error('Error creating plan:', error);
      alert('Error creating plan. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50 dark:from-slate-900 dark:to-slate-800">
      {/* Header */}
      <div className="bg-white/80 dark:bg-slate-900/80 backdrop-blur-sm border-b border-slate-200 dark:border-slate-700">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center gap-4">
            <Button variant="ghost" onClick={() => navigate('/plans')}>
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back to Plans
            </Button>
            <div>
              <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                Create New Travel Plan
              </h1>
              <p className="mt-2 text-slate-600 dark:text-slate-400">
                Plan your next adventure with detailed itinerary and AI assistance
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Form */}
          <div className="lg:col-span-2 space-y-6">
            {/* Basic Information */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <MapPin className="w-5 h-5 text-blue-600" />
                  Basic Information
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Destination *
                  </label>
                  <Input
                    placeholder="Where do you want to go?"
                    value={formData.nameoftheplace}
                    onChange={(e) => handleInputChange('nameoftheplace', e.target.value)}
                    className="text-lg"
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      From Date *
                    </label>
                    <Input
                      type="date"
                      value={formData.fromdate}
                      onChange={(e) => handleInputChange('fromdate', e.target.value)}
                      min={new Date().toISOString().split('T')[0]}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      To Date *
                    </label>
                    <Input
                      type="date"
                      value={formData.todate}
                      onChange={(e) => handleInputChange('todate', e.target.value)}
                      min={formData.fromdate || new Date().toISOString().split('T')[0]}
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Budget (Optional)
                  </label>
                  <div className="relative">
                    <DollarSign className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                    <Input
                      type="number"
                      placeholder="Enter your budget"
                      value={formData.budget}
                      onChange={(e) => handleInputChange('budget', e.target.value)}
                      className="pl-10"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Description (Optional)
                  </label>
                  <textarea
                    placeholder="Tell us about your travel plans..."
                    value={formData.abouttheplace}
                    onChange={(e) => handleInputChange('abouttheplace', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:text-white"
                    rows={3}
                  />
                </div>
              </CardContent>
            </Card>

            {/* Travel Companions */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Users className="w-5 h-5 text-purple-600" />
                  Travel Companions
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                  {companions.map((companion) => (
                    <Button
                      key={companion.id}
                      variant={formData.companion === companion.id ? 'default' : 'outline'}
                      onClick={() => handleInputChange('companion', companion.id)}
                      className="h-12 text-sm"
                    >
                      {companion.label}
                    </Button>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Activity Preferences */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Heart className="w-5 h-5 text-red-600" />
                  Activity Preferences
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                  {activities.map((activity) => {
                    const Icon = activity.icon;
                    const isSelected = selectedActivities.includes(activity.id);
                    
                    return (
                      <Button
                        key={activity.id}
                        variant={isSelected ? 'default' : 'outline'}
                        onClick={() => toggleActivity(activity.id)}
                        className="h-16 flex flex-col gap-1 text-xs"
                      >
                        <Icon className="w-5 h-5" />
                        {activity.label}
                      </Button>
                    );
                  })}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Plan Summary */}
            <Card>
              <CardHeader>
                <CardTitle>Plan Summary</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="flex items-center gap-2 text-sm">
                  <MapPin className="w-4 h-4 text-blue-600" />
                  <span className="text-gray-600">
                    {formData.nameoftheplace || 'No destination selected'}
                  </span>
                </div>
                
                <div className="flex items-center gap-2 text-sm">
                  <Calendar className="w-4 h-4 text-green-600" />
                  <span className="text-gray-600">
                    {formData.fromdate && formData.todate 
                      ? `${new Date(formData.fromdate).toLocaleDateString()} - ${new Date(formData.todate).toLocaleDateString()}`
                      : 'No dates selected'
                    }
                  </span>
                </div>

                {formData.budget && (
                  <div className="flex items-center gap-2 text-sm">
                    <DollarSign className="w-4 h-4 text-yellow-600" />
                    <span className="text-gray-600">${parseFloat(formData.budget).toLocaleString()}</span>
                  </div>
                )}

                {formData.companion && (
                  <div className="flex items-center gap-2 text-sm">
                    <Users className="w-4 h-4 text-purple-600" />
                    <span className="text-gray-600">
                      {companions.find(c => c.id === formData.companion)?.label}
                    </span>
                  </div>
                )}

                {selectedActivities.length > 0 && (
                  <div className="space-y-2">
                    <span className="text-sm font-medium text-gray-700">Activities:</span>
                    <div className="flex flex-wrap gap-1">
                      {selectedActivities.map(activityId => {
                        const activity = activities.find(a => a.id === activityId);
                        return (
                          <Badge key={activityId} variant="secondary" className="text-xs">
                            {activity?.label}
                          </Badge>
                        );
                      })}
                    </div>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Action Buttons */}
            <Card>
              <CardHeader>
                <CardTitle>Create Your Plan</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <Button
                  onClick={() => handleCreatePlan(true)}
                  disabled={loading}
                  className="w-full bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white"
                >
                  {loading ? (
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  ) : (
                    <Wand2 className="w-4 h-4 mr-2" />
                  )}
                  Generate with AI
                </Button>
                
                <Button
                  onClick={() => handleCreatePlan(false)}
                  disabled={loading}
                  variant="outline"
                  className="w-full"
                >
                  {loading ? (
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  ) : (
                    <Plus className="w-4 h-4 mr-2" />
                  )}
                  Create Empty Plan
                </Button>

                <p className="text-xs text-gray-500 text-center">
                  AI generation creates a complete itinerary based on your preferences
                </p>
              </CardContent>
            </Card>

            {/* Tips */}
            <Card>
              <CardHeader>
                <CardTitle className="text-sm">💡 Tips</CardTitle>
              </CardHeader>
              <CardContent className="text-xs text-gray-600 space-y-2">
                <p>• Be specific with your destination for better AI suggestions</p>
                <p>• Select activities that interest you most</p>
                <p>• Budget helps AI suggest appropriate accommodations</p>
                <p>• You can always edit your plan after creation</p>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewPlanPage;
